mapper.update:
update user

    password = #{user.password},
    term_start_date = #{user.termStartDate},
    term_end_date = #{user.termEndDate}

where uid = #{user.uid};

mapper.select:
select uid, username, password, term_start_date
from user
where username = #{username};

select uid, username, term_start_date, term_end_date
from user
where username = #{username} and api_token = #{apiToken};

select password
from user
where username = #{username};

select api_token
from user
where username = #{username};
